import * as fs from 'fs';
import { Pokemon } from './pokemon.class'; // eslint-disable-line
import { PokemonPrint, PokemonActions } from './pokemon.interfaces'; // eslint-disable-line

/**
 * # Combat Class | Primary parent class
 *
 * ## Implements
 *
 * - PokemonPrint
 * - PokemonActions
 *
 * ## Features
 *
 * - firstFighter | Pokemon object (defines a Pokemon and it's features)
 * - secondFighter | Pokemon object (defines a Pokemon and it's features)
 *
 * ## Methods
 * - print(void) | Prints current combat status
 * - attack(Pokemon) | Used to attack the pokemon in parameter
 * - effectiveness(Pokemon, Pokemon) | measures effectiveness between pokemon types
 * - start(void) | Starts a fight between two pokemons
 */
export class Combat implements PokemonPrint, PokemonActions {
  private firstFighter: Pokemon;

  private secondFighter: Pokemon;

  constructor(firstFighter: Pokemon, secondFighter: Pokemon) {
    this.firstFighter = firstFighter;
    this.secondFighter = secondFighter;
  }

  print(): void {
    console.log('\t\n\nCombat status:\n');
    // image
    console.log('\t__________________________________________________________________________________________\n');

    // Second fighter
    console.log(`\tFirst fighter | ${this.firstFighter.getName().toUpperCase()}`);
    try {
      const result = fs.readFileSync(`./images/${this.firstFighter.getName()}.txt`, 'utf8');
      console.log(result);
    } catch {
      const result = fs.readFileSync('./images/unknown.txt', 'utf8');
      console.log(result);
    }
    console.log(`\tHP: ${this.firstFighter.getStats()[0]} \t\t| [${Array(this.firstFighter.getStats()[0]).fill('#').join('')}]`);

    console.log('\t\t\n----------------------------------------------\n');

    // Second fighter
    console.log(`\tSecond fighter | ${this.secondFighter.getName().toUpperCase()}`);
    try {
      const result = fs.readFileSync(`./images/${this.secondFighter.getName()}.txt`, 'utf8');
      console.log(result);
    } catch {
      const result = fs.readFileSync('./images/unknown.txt', 'utf8');
      console.log(result);
    }
    // stats
    console.log(`\tHP: ${this.secondFighter.getStats()[0]} \t\t| [${Array(this.secondFighter.getStats()[0]).fill('#').join('')}]`);
    console.log('\t__________________________________________________________________________________________\n');
  }

  attack(first: Pokemon): void {
    const receiver: Pokemon = (this.firstFighter === first)
      ? this.firstFighter
      : this.secondFighter;
    const attacker: Pokemon = (this.firstFighter === first)
      ? this.secondFighter
      : this.firstFighter;

    receiver.setHp(receiver.getStats()[0] - 50
      * (attacker.getStats()[1] / receiver.getStats()[2])
      * (this.effectiveness(attacker, receiver)));
  }

  effectiveness(first: Pokemon, second: Pokemon): 1 | 0.5 | 2 {
    if (first.getType() === second.getType()) { return 1; }

    switch (first.getType()) {
      case 'fire':
        if (second.getType() === 'water') { return 0.5; }
        if (second.getType() === 'leaf') { return 2; }
        if (second.getType() === 'electric') { return 1; }
        break;
      case 'water':
        if (second.getType() === 'leaf') { return 0.5; }
        if (second.getType() === 'fire') { return 2; }
        if (second.getType() === 'electric') { return 0.5; }
        break;
      case 'leaf':
        if (second.getType() === 'fire') { return 0.5; }
        if (second.getType() === 'water') { return 2; }
        if (second.getType() === 'electric') { return 1; }
        break;
      case 'electric':
        if (second.getType() === 'fire') { return 0.5; }
        if (second.getType() === 'water') { return 2; }
        if (second.getType() === 'leaf') { return 1; }
        break;
      default:
        return 1;
    }
    return 1;
  }

  start(): Pokemon {
    let i: number = 1;
    while (this.firstFighter.getStats()[0] > 0
       && this.secondFighter.getStats()[0] > 0) {
      console.log(`COMBAT ROUND: ${i}`);
      console.log(`${this.firstFighter.getName()} attaks ${this.secondFighter.getName()}`);
      if (this.firstFighter.getStats()[0] > 0
      && this.secondFighter.getStats()[0] > 0) {
        this.attack(this.secondFighter);
      }
      this.print();
      console.log(`${this.secondFighter.getName()} attaks ${this.firstFighter.getName()}`);
      if (this.firstFighter.getStats()[0] > 0
      && this.secondFighter.getStats()[0] > 0) {
        this.attack(this.firstFighter);
      }
      this.print();
      i += 1;
    }
    return this.firstFighter.getStats()[0] <= 0
      ? this.secondFighter
      : this.firstFighter;
  }
}

import { PokemonType, Measures, Stats } from "./pokemon.interfaces"; // eslint-disable-line

/**
 * # Pokemon Class | Primary parent class
 *
 * ## Features
 *
 * - name | String with pokemon name
 * - type | PokemonType (contains pokemon type)
 * - shape | Object of numbers (defines the pokemon height and weight in that order)
 * - stats | Object of numbers
 * (defines the pokemon health points (hp), attack (atk), defense
 *  (def)  and speed (spd) in that order)
 *
 * ## Methods
 *
 * - getName(void) | Returns String with pokemon name
 * - getShape(void) | Returns Object of number with pokemon shape
 * - getType(void) | Returns String with pokemon type
 * - getStats(void) | Returns Object of number with pokemon stats
 * - setHp(number) | Sets pokemon HP to value in parameter
 */
export class Pokemon {
  protected readonly name: string;

  protected readonly shape: Measures;

  protected readonly type: PokemonType;

  protected stats: Stats;

  constructor(
    name: string = '',
    type: PokemonType = 'normal',
    shape: Measures = { height: 0, weight: 0 },
    stats: Stats = {
      hp: 0,
      atk: 0,
      def: 0,
      spd: 0,
    },
  ) {
    this.name = name.toLowerCase();
    this.shape = shape;
    this.type = type;
    this.stats = stats;
  }

  getName(): string {
    return this.name;
  }

  getShape(): [number, number] {
    return [this.shape.height, this.shape.height];
  }

  getType(): PokemonType {
    return this.type;
  }

  getStats(): [number, number, number, number] {
    return [
      this.stats.hp,
      this.stats.atk,
      this.stats.def,
      this.stats.spd,
    ];
  }

  setHp(value: number): void {
    this.stats.hp = value;
  }
}

import * as fs from 'fs';
import { Pokemon } from './pokemon.class'; // eslint-disable-line
import { PokemonPrint } from './pokemon.interfaces'; // eslint-disable-line

/**
 * # Pokedex Class | Primary parent class
 *
 * ## Implements
 *
 * - PokemonPrint
 *
 * ## Features
 *
 * - list | Pokemon object array (defines an array of pokemons)
 *
 * ## Methods
 *
 * - isEmpty(void) | Returns true if list is empty
 * - print(void) | Prints all pokemon in pokedex and their status
 * - addPokemon(Pokemon) | inserts a pokemon in the list if it's not registered
 * - removePokemon(String) | removes a pokemon from the list by it's name if it's registered
 */
export class PokeDex implements PokemonPrint {
  private list: Pokemon[];

  constructor(list: Pokemon[] = []) {
    this.list = list;
  }

  isEmpty(): boolean {
    return this.list.length === 0;
  }

  print(): void {
    console.log('\t\n\n\nPrinting pokedex content ...\n\n\n\n');
    console.log(`\tStored ${this.list.length} pokemons in your pokedex \n`);
    this.list.forEach((pokemon, i) => {
      // image
      console.log('\t__________________________________________________________________________________________\n');
      console.log(`\t${i + 1} | ${pokemon.getName().toUpperCase()}`);
      try {
        const result = fs.readFileSync(`./images/${pokemon.getName()}.txt`, 'utf8');
        console.log(result);
      } catch {
        const result = fs.readFileSync('./images/unknown.txt', 'utf8');
        console.log(result);
      }
      // stats
      console.log(`\tHP: ${pokemon.getStats()[0]} \t\t| [${Array(pokemon.getStats()[0]).fill('#').join('')}]`);
      console.log(`\tAttak: ${pokemon.getStats()[1]} \t| [${Array(pokemon.getStats()[1]).fill('#').join('')}]`);
      console.log(`\tDefense: ${pokemon.getStats()[2]} \t| [${Array(pokemon.getStats()[2]).fill('#').join('')}]`);
      console.log(`\tSpeed: ${pokemon.getStats()[3]} \t| [${Array(pokemon.getStats()[3]).fill('#').join('')}]`);
      console.log('\t__________________________________________________________________________________________\n');
    });
  }

  addPokemon(newPokemon: Pokemon): boolean {
    if (this.list.includes(newPokemon)) { return false; }
    this.list.push(newPokemon);
    return true;
  }

  removePokemon(name: string): boolean {
    if (this.list.filter((el) => el.getName() === name.toLowerCase()).length === 0) {
      return false;
    }
    this.list = this.list
      .filter((el) => el.getName() !== name.toLowerCase());
    return true;
  }
}

import { Token, GameStatus } from "./game.interfaces"; // eslint-disable-line

const forbidenUpDiagonals: Token[] = [
  { i: 0, j: 0 },
  { i: 0, j: 1 },
  { i: 0, j: 2 },
  { i: 1, j: 0 },
  { i: 1, j: 1 },
  { i: 2, j: 0 },
  { i: 5, j: 4 },
  { i: 5, j: 5 },
  { i: 5, j: 6 },
  { i: 4, j: 5 },
  { i: 4, j: 6 },
  { i: 3, j: 6 },
];

const forbidenDownDiagonals: Token[] = [
  { i: 0, j: 4 },
  { i: 0, j: 5 },
  { i: 0, j: 6 },
  { i: 1, j: 5 },
  { i: 1, j: 6 },
  { i: 2, j: 6 },
  { i: 5, j: 0 },
  { i: 5, j: 1 },
  { i: 5, j: 2 },
  { i: 4, j: 0 },
  { i: 4, j: 1 },
  { i: 3, j: 0 },
];

/**
 * # Connect four player Class | Primary parent class
 *
 * ## Implements
 *
 * - GameStatus
 *
 * ## Features
 *
 * - name | String (stores player name)
 * - color | String (stores player custom color)
 * - tokens | Array of tokens (marks tokens positions in the board)
 *
 * ## Methods
 *
 * - getName(void) | Returns player name
 * - getcolor(void) | Returns player color
 * - getTokens(void) | Returns player array of played tokens
 * - isWinner(void) | Returns true if player wins connecting four tokens
 * - makeMove(position: Token) | stores new token in played tokens
 */

export class ConnectFourPlayer implements GameStatus {
  private name: string;

  private color: string;

  private tokens: Token[];

  constructor(name: string = '', color: string = '') {
    this.name = name;
    this.color = color;
    this.tokens = [];
  }

  getName(): string { return this.name; }

  getColor(): string { return this.color; }

  getTokens(): Token[] { return this.tokens; }

  isWinner(): boolean {
    if (this.tokens.length < 4) { return false; }
    let status: boolean = false;
    // check horizontal
    this.tokens
      .sort((a, b) => a.i - b.i)
      .forEach((el, pos) => {
        if (pos < this.tokens.length - 3) {
          if (((el.i) === this.tokens[pos + 1].i
            && (el.i) === this.tokens[pos + 2].i
            && (el.i) === this.tokens[pos + 3].i)
            && ((Math.abs((el.j) - this.tokens[pos + 1].j) === 1)
            && (Math.abs((el.j + 1) - this.tokens[pos + 2].j) === 1)
            && (Math.abs((el.j + 2) - this.tokens[pos + 3].j) === 1))) {
            status = true;
          }
        }
      });
    // check vertical
    this.tokens
      .sort((a, b) => a.j - b.j)
      .forEach((el, pos) => {
        if (pos < this.tokens.length - 3) {
          if (((el.j) === this.tokens[pos + 1].j
            && (el.j) === this.tokens[pos + 2].j
            && (el.j) === this.tokens[pos + 3].j)
            && ((Math.abs((el.i) - this.tokens[pos + 1].i) === 1)
            && (Math.abs((el.i + 1) - this.tokens[pos + 2].i) === 1)
            && (Math.abs((el.i + 2) - this.tokens[pos + 3].i) === 1))) {
            status = true;
          }
        }
      });

    // check up diagonal
    this.tokens
      // Only up diagonal allowed tokens
      .filter((el) => {
        let pass: boolean = true;
        forbidenUpDiagonals.forEach((forbiden) => {
          if ((forbiden.i === el.i) && (forbiden.j === el.j)) {
            pass = false;
          }
        });
        return pass;
      })
      .forEach((el) => {
        let counter: number = 0;
        this.tokens.forEach((token) => {
          if ((token.i === (el.i)) && (token.j === (el.j))) {
            counter += 1;
          }
          if ((token.i === (el.i - 1)) && (token.j === (el.j + 1))) {
            counter += 1;
          }
          if ((token.i === (el.i - 2)) && (token.j === (el.j + 2))) {
            counter += 1;
          }
          if ((token.i === (el.i - 3)) && (token.j === (el.j + 3))) {
            counter += 1;
          }
        });
        if (counter === 4) { status = true; }
      });

    // Check down diagonal
    this.tokens
      // Only up diagonal allowed tokens
      .filter((el) => {
        let pass: boolean = true;
        forbidenDownDiagonals.forEach((forbiden) => {
          if ((forbiden.i === el.i) && (forbiden.j === el.j)) {
            pass = false;
          }
        });
        return pass;
      })
      .forEach((el) => {
        let counter: number = 0;
        this.tokens.forEach((token) => {
          if ((token.i === (el.i)) && (token.j === (el.j))) {
            counter += 1;
          }
          if ((token.i === (el.i + 1)) && (token.j === (el.j + 1))) {
            counter += 1;
          }
          if ((token.i === (el.i + 2)) && (token.j === (el.j + 2))) {
            counter += 1;
          }
          if ((token.i === (el.i + 3)) && (token.j === (el.j + 3))) {
            counter += 1;
          }
        });
        if (counter === 4) { status = true; }
      });
    return status;
  }

  makeMove(position: Token): boolean {
    if (this.tokens
      .filter((el) => (el.i === position.i) && (el.j === position.j))
      .length > 0) {
      return false;
    }
    this.tokens.push(position);
    return true;
  }
}

import * as rl from 'readline-sync';
import { ConnectFourPlayer } from "./connectFourPlayer.class"; // eslint-disable-line
import { GameActions, GameStatus, PrintableGame } from "./game.interfaces"; // eslint-disable-line

/**
 * # Connect four Game Class | Primary parent class
 *
 * ## Implements
 *
 * - GameStatus
 * - GameActions
 * - PrintableGame
 *
 * ## Features
 *
 * - playerA | ConnectFourPlayer object (stores token positions)
 * - playerB | ConnectFourPlayer object (stores token positions)
 * - slots | Array of booleans (marks played positions)
 *
 * ## Methods
 * - runGame(void) | Starts a game between the two players
 */

export class ConnectFourGame implements GameStatus, GameActions, PrintableGame {
  private playerA: ConnectFourPlayer;

  private playerB: ConnectFourPlayer;

  private slots: boolean[][];

  constructor(playerA: ConnectFourPlayer, playerB: ConnectFourPlayer) {
    this.playerA = playerA;
    this.playerB = playerB;
    this.slots = [
      [true, true, true, true, true, true, true],
      [true, true, true, true, true, true, true],
      [true, true, true, true, true, true, true],
      [true, true, true, true, true, true, true],
      [true, true, true, true, true, true, true],
      [true, true, true, true, true, true, true],
    ];
  }

  isTie(): boolean {
    return this.playerA.getTokens().length + this.playerA.getTokens().length === 42;
  }

  print(): void {
    console.log(
      `Game between [31m${this.playerA.getName()}[37m and [33m${this.playerB.getName()}[37m `
      + `| ${this.playerA.getTokens().length + this.playerB.getTokens().length} tokens inside`,
    );
    let rowString: string = '';
    rowString += '\n  1   2   3   4   5   6   7  \n';
    rowString += '\n';
    this.slots.forEach((row, i) => {
      rowString += '';
      row.forEach((col, j) => {
        if (this.playerA.getTokens().filter((el) => el.i === i && el.j === j).length > 0) {
          rowString += ' [31m[37m ';
        } else if (this.playerB.getTokens().filter((el) => el.i === i && el.j === j).length > 0) {
          rowString += ' [33m[37m ';
        } else {
          rowString += '   ';
        }
      });
      rowString += '\n';

      if (i !== 5) {
        rowString += '\n';
      }
    });
    rowString += '\n';
    console.log(rowString);
  }

  insertToken(col: number, player: ConnectFourPlayer): boolean {
    if (this.getColumn(col - 1).filter((el) => el).length === 0) { return false; }
    let moved: boolean = false;
    this.getColumn(col - 1)
      .reverse()
      .forEach((el, row) => {
        if ((el === true) && (moved === false)) {
          moved = true;
          this.slots[5 - row][col - 1] = false;
          player.makeMove({ i: 5 - row, j: col - 1 });
        }
      });
    return true;
  }

  runGame(): void {
    while (!this.isTie()
    && !this.playerA.isWinner()
    && !this.playerB.isWinner()) {
      let ok: boolean = false;
      let column: string = '';
      while (!ok) {
        column = rl.question(`It's ${this.playerA.getName()}'s turn, insert a column: `);
        if ((+column > 0) && (+column <= 7)) {
          if (this.insertToken(+column, this.playerA)) {
            this.print();
            ok = true;
            if (this.isTie() || this.playerA.isWinner()) {
              break;
            }
          } else {
            console.log(`Column ${+column} is full, please choose another one ...`);
          }
        } else {
          console.log(`Column ${+column} is out of boundries, please choose another one ...`);
        }
      }

      if (this.isTie() || this.playerA.isWinner()) {
        break;
      }

      ok = false;

      while (!ok) {
        column = rl.question(`It's ${this.playerB.getName()}'s turn, insert a column: `);
        if ((+column > 0) && (+column <= 7)) {
          if (this.insertToken(+column, this.playerB)) {
            this.print();
            if (this.isTie()
              || this.playerA.isWinner()
              || this.playerB.isWinner()) {
              break;
            }
            ok = true;
          } else {
            console.log(`Column ${+column} is full, please choose another one ...`);
          }
        } else {
          console.log(`Column ${+column} is out of boundries, please choose another one ...`);
        }
      }

      if (this.isTie()
        || this.playerA.isWinner()
        || this.playerB.isWinner()) {
        break;
      }
    }
    if (this.playerA.isWinner()) {
      console.log(`Wins ${this.playerA.getName()}!!!!`);
    }
    if (this.playerB.isWinner()) {
      console.log(`Wins ${this.playerB.getName()}!!!!`);
    }
    if (this.isTie()) {
      console.log('There was a tie between the players ...');
    }
  }

  getColumn(col: number): boolean[] {
    const column: boolean[] = [];
    for (let i: number = 0; i < 6; i += 1) {
      for (let j: number = 0; j < 7; j += 1) {
        if (j === col) {
          column.push(this.slots[i][j]);
        }
      }
    }
    return column;
  }

  getBoard(): boolean[][] { return this.slots; }
}

import {
  Operable,
  ToNatural,
  ParsableObject,
  ToStringValue,
} from './hexadecimal.interfaces'; // eslint-disable-line

/**
 * # Hexadecimal | Primary Class
 */
export class Hexadecimal implements Operable, ToNatural, ParsableObject, ToStringValue {
  private readonly naturalNum: number;

  constructor(naturalNum: number = 0) {
    this.naturalNum = naturalNum;
  } // eslint-disable-line

  add(objB: Hexadecimal): Hexadecimal {
    return new Hexadecimal(this.naturalNum + objB.valueOf());
  }

  subtract(objB: Hexadecimal): Hexadecimal {
    return new Hexadecimal(this.naturalNum - objB.valueOf());
  }

  valueOf(): number {
    return this.naturalNum;
  }

  toString(): string {
    return `0x${this.naturalNum.toString(16)}`;
  }

  parse(obj: string): number { return +obj; }
}
